

    let btn = document.getElementById("submit");
    let na = document.getElementById("firma");
    let ref = document.getElementById("reference");

btn.addEventListener("click",(event)=>{
    localStorage.clear()
    if(btn.value=="submit"){

        if(check(na.value)&&check(ref.value)&&juud(ref.value)){
            localStorage.setItem("firma",na.value+" "+ref.value)
            localStorage.setItem("src",window.event.srcElement.id)
            let s1 = localStorage.getItem("firma")
            let s2 = localStorage.getItem("src")
           if(s1!=""&&s2!=""){
            localStorage.setItem("loginzaman",new Date().getSeconds())
            localStorage.setItem("logintime",new Date().getMinutes())
            window.location.assign("./public/home.html")

           }else{
                alert("Cookies are deactivated !")
           }
            
        }else{
            alert("Please fill the inputs correctly !")
        }
    }
})

function check(x){
    return((x!=null)&&(x!="")&&((na.value).length<30&&(na.value).length>3)&&(ref.value).length==6)?true:false;
       
}
